  render: () => <div className="space-y-6">
      <div>
        <h3 className="text-lg font-semibold mb-4">Solid Buttons</h3>
        <div className="flex flex-col gap-7">
          <div className="space-y-3">
            <h4 className="text-sm font-medium">Primary</h4>
            <div className="space-y-2 flex gap-2 items-start">
              <Button variant="solid" hierarchy="primary" size="lg">
                Large
              </Button>
              <Button variant="solid" hierarchy="primary" size="lg" disabled>
                Large Disabled
              </Button>
              <Button variant="solid" hierarchy="primary" size="md">
                Medium
              </Button>
              <Button variant="solid" hierarchy="primary" size="md" disabled>
                Medium Disabled
              </Button>
              <Button variant="solid" hierarchy="primary" size="sm">
                Small
              </Button>
              <Button variant="solid" hierarchy="primary" size="sm" disabled>
                Small Disabled
              </Button>
            </div>
          </div>

          <div className="space-y-3">
            <h4 className="text-sm font-medium">Secondary</h4>
            <div className="space-y-2 flex gap-2 items-start">
              <Button variant="solid" hierarchy="secondary" size="lg">
                Large
              </Button>
              <Button variant="solid" hierarchy="secondary" size="lg" disabled>
                Large Disabled
              </Button>
              <Button variant="solid" hierarchy="secondary" size="md">
                Medium
              </Button>
              <Button variant="solid" hierarchy="secondary" size="md" disabled>
                Medium Disabled
              </Button>
              <Button variant="solid" hierarchy="secondary" size="sm">
                Small
              </Button>
              <Button variant="solid" hierarchy="secondary" size="sm" disabled>
                Small Disabled
              </Button>
            </div>
          </div>

          <div className="space-y-3">
            <h4 className="text-sm font-medium">Assistive</h4>
            <div className="space-y-2 flex gap-2 items-start">
              <Button variant="solid" hierarchy="assistive" size="lg">
                Large
              </Button>
              <Button variant="solid" hierarchy="assistive" size="lg" disabled>
                Large Disabled
              </Button>
              <Button variant="solid" hierarchy="assistive" size="md">
                Medium
              </Button>
              <Button variant="solid" hierarchy="assistive" size="md" disabled>
                Medium Disabled
              </Button>
              <Button variant="solid" hierarchy="assistive" size="sm">
                Small
              </Button>
              <Button variant="solid" hierarchy="assistive" size="sm" disabled>
                Small Disabled
              </Button>
            </div>
          </div>

          <div className="space-y-3">
            <h4 className="text-sm font-medium">Custom</h4>
            <div className="space-y-2  flex gap-2 items-start">
              <Button variant="solid" hierarchy="custom" size="lg">
                Large
              </Button>
              <Button variant="solid" hierarchy="custom" size="lg" disabled>
                Large Disabled
              </Button>
              <Button variant="solid" hierarchy="custom" size="md">
                Medium
              </Button>
              <Button variant="solid" hierarchy="custom" size="md" disabled>
                Medium Disabled
              </Button>
              <Button variant="solid" hierarchy="custom" size="sm">
                Small
              </Button>
              <Button variant="solid" hierarchy="custom" size="sm" disabled>
                Small Disabled
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
